mapper.select:
SELECT id, is_deleted, customer_name, customer_age, customer_sex, idcard, room_no, building_no, checkin_date, expiration_date, contact_tel, bed_id, psychosomatic_state, attention, birthday, height, weight, blood_type, filepath, user_id, level_id, family_member FROM customer WHERE is_deleted = 0
AND user_id = #{roleId}
AND customer_name LIKE CONCAT('%', #{nickname}, '%')
LIMIT #{pageSize} OFFSET #{(currentPag - 1) * pageSize}

SELECT COUNT(*) FROM customer WHERE is_deleted = 0
AND user_id = #{roleId}
AND customer_name LIKE CONCAT('%', #{nickname}, '%')
